operations.reducer:
const newProject = {
  id: action.input.id,
  name: action.input.name,
  path: null,
  currentStatus: 'MISSING',
  targetedStatus: 'STOPPED',
  configuration: {
    connectPort: action.input.connectPort || 5000,
    switchboardPort: action.input.switchboardPort || 6100,
    startupTimeout: 30000,
    autoStart: false
  },
  runtime: null,
  logs: []
};
state.projects.push(newProject);

operations.schema:
input CreateProjectInput {
  id: OID!
  name: String!
  connectPort: Int
  switchboardPort: Int
}

operations.reducer:
const project = state.projects.find(p => p.id === action.input.projectId);
if (!project) {
  throw new ProjectNotFoundError(`Project with ID ${action.input.projectId} not found`);
}

// Smart logic: Only one project can have RUNNING target
// Stop all other projects that are targeted to run
state.projects.forEach(p => {
  if (p.id !== action.input.projectId && p.targetedStatus === 'RUNNING') {
    p.targetedStatus = 'STOPPED';
  }
});

project.targetedStatus = 'RUNNING';

const project = state.projects.find(p => p.id === action.input.projectId);
if (!project) {
  throw new ProjectNotFoundError(`Project with ID ${action.input.projectId} not found`);
}
project.targetedStatus = 'STOPPED';

const project = state.projects.find(p => p.id === action.input.projectId);
if (!project) {
  throw new ProjectNotFoundError(`Project with ID ${action.input.projectId} not found`);
}

project.targetedStatus = 'DELETED';

// Smart logic: Auto-reconcile DELETED status
// If current status is MISSING and we target DELETED, it's effectively deleted
if (project.currentStatus === 'MISSING') {
  project.currentStatus = 'DELETED';
}

const existingProject = state.projects.find(p => p.name === action.input.name || p.path === action.input.path);
if (existingProject) {
  throw new DuplicateProjectError(`Project with name '${action.input.name}' or path '${action.input.path}' already exists`);
}

const newProject = {
  id: action.input.id,
  name: action.input.name,
  path: action.input.path,
  currentStatus: action.input.currentStatus,
  targetedStatus: 'STOPPED',
  configuration: {
    connectPort: action.input.connectPort,
    switchboardPort: action.input.switchboardPort,
    startupTimeout: action.input.startupTimeout,
    autoStart: action.input.autoStart
  },
  runtime: null,
  logs: []
};
state.projects.push(newProject);

const project = state.projects.find(p => p.id === action.input.projectId);
if (!project) {
  throw new ProjectNotFoundError(`Project with ID ${action.input.projectId} not found`);
}
if (action.input.connectPort !== undefined && action.input.connectPort !== null) {
  project.configuration.connectPort = action.input.connectPort;
}
if (action.input.switchboardPort !== undefined && action.input.switchboardPort !== null) {
  project.configuration.switchboardPort = action.input.switchboardPort;
}
if (action.input.startupTimeout !== undefined && action.input.startupTimeout !== null) {
  project.configuration.startupTimeout = action.input.startupTimeout;
}
if (action.input.autoStart !== undefined && action.input.autoStart !== null) {
  project.configuration.autoStart = action.input.autoStart;
}

const project = state.projects.find(p => p.id === action.input.projectId);
if (!project) {
  throw new ProjectNotFoundError(`Project with ID ${action.input.projectId} not found`);
}

const previousStatus = project.currentStatus;
project.currentStatus = action.input.currentStatus;

// Update path if provided
if (action.input.path !== undefined && action.input.path !== null) {
  project.path = action.input.path;
}

// Smart logic: Clear runtime info when stopping
// If transitioning to STOPPED, clear the runtime info
if (action.input.currentStatus === 'STOPPED' && previousStatus === 'RUNNING') {
  project.runtime = null;
}

operations.schema:
input UpdateProjectStatusInput {
  projectId: OID!
  currentStatus: CurrentStatus!
  path: String
}

operations.reducer:
const project = state.projects.find(p => p.id === action.input.projectId);
if (!project) {
  throw new ProjectNotFoundError(`Project with ID ${action.input.projectId} not found`);
}

project.runtime = {
  pid: action.input.pid,
  startedAt: action.input.startedAt,
  driveUrl: action.input.driveUrl || null,
  connectPort: action.input.connectPort,
  switchboardPort: action.input.switchboardPort
};

const project = state.projects.find(p => p.id === action.input.projectId);
if (!project) {
  throw new ProjectNotFoundError(`Project with ID ${action.input.projectId} not found`);
}
const logEntry = {
  id: action.input.id,
  timestamp: action.input.timestamp,
  message: action.input.message
};
project.logs.push(logEntry);

operations.schema:
input AddLogEntryInput {
  projectId: OID!
  timestamp: DateTime!
  message: String!
}

operations.reducer:
const project = state.projects.find(p => p.id === action.input.projectId);
if (!project) {
  throw new ProjectNotFoundError(`Project with ID ${action.input.projectId} not found`);
}
project.logs = [];

global.schema:
type AgentProjectsState {
  projects: [Project!]!
}

type Project {
  id: OID!
  name: String!
  currentStatus: CurrentStatus!
  targetedStatus: TargetedStatus!
  configuration: ProjectConfig!
  path: String
  runtime: RuntimeInfo
  logs: [LogEntry!]!
}

enum TargetedStatus {
  STOPPED
  RUNNING
  DELETED
}

enum CurrentStatus {
  MISSING
  INITIALIZING
  STOPPED
  RUNNING
  DELETED
}

type ProjectConfig {
  connectPort: Int!
  switchboardPort: Int!
  startupTimeout: Int!
  autoStart: Boolean!
}

type RuntimeInfo {
  pid: Int!
  startedAt: DateTime!
  driveUrl: String
  connectPort: Int!
  switchboardPort: Int!
}

type LogEntry {
  timestamp: DateTime!
  message: String!
}
